        map!!.setOnMarkerClickListener(object : GoogleMap.OnMarkerClickListener {
            override fun onMarkerClick(marker: Marker): Boolean {
                card_view.visibility = View.VISIBLE

                if(marker.title.equals("샹차이") || marker.alpha == 0.98f) {
                    storeInfo.text = "내용: "
                    makeOffBt.visibility = View.GONE
                    gatherBt.visibility = View.VISIBLE
                }
                else {
                    storeInfo.text = "가게 정보: "
                    makeOffBt.visibility = View.VISIBLE
                    gatherBt.visibility = View.GONE
                }



                var storeTv = findViewById<TextView>(com.hanium.R.id.storeTv)
                var contentTv = findViewById<TextView>(com.hanium.R.id.contentTv)

                var temp = marker.tag
                storeTv.text = marker.title
                contentTv.text = marker.snippet

                Glide.with(this@MapActivity)
                    .load(temp)
                    .into(storeImg)

                currentMarker = marker



                return false
            }
        })


        map!!.setOnMapClickListener(object : GoogleMap.OnMapClickListener {
            override fun onMapClick(latLng: LatLng) {
